import { getProductDetails } from '../services/ProductDetails'
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react'
import "./item.css";

function Item() {
    
    const [itemdetails, setitemdetails] = useState([]);
    const { id } = useParams();
    console.log("item id : "+id);

    useEffect(() => {
        // get the list of products from server
        loadProductsDetails()
      }, [])

    const loadProductsDetails = async () => {
        const response = await getProductDetails(id)
        console.log(response)  
        setitemdetails(response)
      }

      const loginUser = async () => {
        if (email.length == '') {
          toast.error('Please enter email')
        } else if (password.length == '') {
          toast.error('Please enter password')
        } else {
          // call register api
          const response = await loginUserApi(email, password)
          console.log("data1 : "+ response);
          // parse the response
          // if (response['status'] === 'success') {
            if (response['jwt'].length > 0) {
              console.log("check")
            // parse the response's data and extract the token
            // const { token, name, mobile, profileImage } = response['data']
            console.log("data : "+response);
            const {first_name,email,jwt }= response
            // const  jwt = response['data']
            // store the token for making other apis
            // sessionStorage['token'] = token
            // sessionStorage['name'] = name
            // sessionStorage['mobile'] = mobile
            // sessionStorage['profileImage'] = profileImage
    
            sessionStorage['first_name'] = first_name
            sessionStorage['email'] = email
            // sessionStorage['user'] = user
            sessionStorage['jwt'] = jwt
    
            // update global store's authSlice with status = true
            dispatch(login())
    
            toast.success(`Welcome ${first_name} to store application`)
    
            // go back to login
            navigate('/product-gallery')
          } else {
            toast.error('Invalid user name or password')
          }
        }
      }


    return(

<div className="maindiv">

<div className="imgdiv"> 

</div>

<div className="itemdiv">
    <h4>{itemdetails['company']}</h4>
    <h3>{itemdetails['productName']}</h3>
    <hr/>
    <h5>M.R.P â‚¹&nbsp; {itemdetails['price']}</h5>
    <h5 style={{display:'flex'}}>Add quantity
    &nbsp;
        <div className="incdec">
            <button className="dec" onClick={dec}>-</button>
            <div className="mid">1</div>
            <button className="inc" onClick={inc}>+</button>
        </div>
    </h5>
    <h6>Description About this product:-</h6>
    <p>{itemdetails['productDescription']}</p>
    <p>Solid color polyester/linen full blackout thick sunscreen floor curtain Type: General Pleat Applicable Window Type: Flat Window Format: Rope Opening and Closing Method: Left and Right Biparting Open Processing Accessories Cost: Included Installation Type: Built-in Function: High Shading(70%-90%) Material: Polyester / Cotton Style: Classic Pattern: Embroidered Location: Window Technics: Woven Use: Home, Hotel, Hospital, Cafe, Office Feature: Blackout, Insulated, Flame Retardant Place of Origin: India Name: Curtain Usage: Window Decoration Keywords: Ready Made Blackout Curtain</p>

<button className="btn btn-primary" onClick={buynow} >Buy Now</button>&nbsp;
<button className="btn btn-primary" onClick={addtocart} >Add to Cart</button>
</div>


</div>
    );
}

export default Item;
